import axios from "axios";

const API = axios.create({baseURL:"http://localhost:5000"})

API.interceptors.request.use((req)=>{
    if(localStorage.getItem('profile')){
        req.headers.authorization = `Bearer ${JSON.parse(localStorage.getItem('profile')).token}`
    }
    return req;
})

export const fetchPost = () => API.get('/posts');
export const fetchPostsBySearch = (searchQuery) => API.get(`/posts/search?searchQuery=${searchQuery.search || 'none'}/tags=${searchQuery.tags}`);
export const createPost =(newPost)=> API.post('/posts',newPost,{token:JSON.parse(localStorage.getItem('profile')).result.googleId});
export const updatePost = (id,updatedPost) => API.patch(`/posts/${id}`,updatedPost);
export const deletePost = (id)=> API.delete(`/posts/${id}`);
export const likePost = (id) => API.patch(`/posts/${id}/likePost`,{token:JSON.parse(localStorage.getItem('profile')).result.googleId});

export const signIn=  (formData) => API.post('/user/signIn',formData)
export const signUp=  (formData) => API.post('/user/signUp',formData)
