import * as api from '../../api';
import { CREATE, DELETE, END_LOADING, FETCH_ALL, FETCH_BY_SEARCH, FETCH_POST, LIKE, START_LOADING, UPDATE } from '../constants';

//action create => function that return an action:

export const getPost =(id)=> async(dispatch)=>{
    try {
        dispatch({type:START_LOADING})
        const {data} = await api.fetchPost(id);
        dispatch({type: FETCH_POST, payload: data});
        dispatch({type: END_LOADING})
    } catch (error) {
        console.log(error);
    }
}

export const getPosts =(page)=> async(dispatch)=>{
    try {
        dispatch({type:START_LOADING})
        const {data} = await api.fetchPosts(page);
        dispatch({type: FETCH_ALL, payload: data});
        dispatch({type: END_LOADING})
    } catch (error) {
        console.log(error);
    }
}

export const getPostsBySearch =(searchQuery)=> async(dispatch)=>{
    try {
        dispatch({type:START_LOADING});
        const {data : {data}} = await api.fetchPostsBySearch(searchQuery);
        dispatch({type: FETCH_BY_SEARCH, payload: data});
        dispatch({type: END_LOADING})
        console.log(data);
    } catch (error) {
        console.log(error )
    }
}

export const createPosts =(post,history)=> async(dispatch)=>{
    try {
        console.log('postcreate', post)
        dispatch({type:START_LOADING});
        const {data} = await api.createPost(post);
        history.push(`/posts/${data._id}`);
        dispatch({type: CREATE, payload: data});
        dispatch({type: END_LOADING})
    } catch (error) {
        console.log(error);
    }
}

export const updatePost = (id,post)=> async (dispatch)=>{
    try{
        console.log('hellopoooooo')
        const {data} = await api.updatePost(id,post);

        console.log('dataUpdatePost',data)
        dispatch({type: UPDATE, payload: data})
    }catch(error){
        console.log(error)
    }
}

export const deletePost =(id)=> async (dispatch)=>{
    try{
        await api.deletePost(id);

        dispatch({type: DELETE, payload: id});
    }catch(error){
        console.log(error)
    }
}

export const likePost =(id)=> async (dispatch)=>{
    try {
        console.log('id========',id)
        const {data} = await api.likePost(id);

        dispatch({type: LIKE, payload: data})
    } catch (error) {
        console.log(error)
    }
}

export const commentPost =(value,id) => async (dispatch)=>{
    try{
        const {data} = await api.comment(value,id);
    }catch(error){
        console.log(error);
    }
}
