import React, { useEffect, useState } from 'react'
import {Container,Typography,AppBar,Grow,Grid} from '@material-ui/core';

import logo from './images/logo.png';
import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import useStyles from './style';
import { getPosts } from './redux_store/actions/posts';
import { useDispatch } from 'react-redux';

const App = () => {
  const [currentId,setCurrentId] = useState(null);
  const classes = useStyles();
  const dispatch = useDispatch()

  useEffect(()=>{
    dispatch(getPosts())
  },[dispatch]);
  return (
    <Container maxWidth="lg">
      <AppBar className={classes.appBar} position='static' color='inherit'>
        <Typography className={classes.heading} variant='h2' align='center'>MemoShare</Typography>
        <img className={classes.image} src={logo} alt="memories" height="60"/>
      </AppBar>
      <Grow in>
        <Grid container justifyContent="space-between" alignItems='stretch' spacing={3}>
          <Grid item xs={12}  sm={7}>
            <Posts setCurrentId={setCurrentId}/>
          </Grid>
          <Grid item xs={12}  sm={4}>
            <Form currentId={currentId}  setCurrentId={setCurrentId}/>
          </Grid>
        </Grid>
      </Grow>
    </Container>
    )
}

export default App
